function onFire() {
    if(!this.get("Morph")._renderContext.originNode) {
        return;
    }
    var innerHTML = this.get("Morph")._renderContext.originNode.innerHTML;
    if(this.get("formatAttributesCheckBox").checked){
        this.get("HTML").textString = this.formatXmlAndAttributes(innerHTML);
    } else {
        this.get("HTML").textString = this.formatXml(innerHTML);
    }
    this.get("HTML").enableSyntaxHighlighting();
}

