function formatXmlAndAttributes(xml) {
    var reg = /(>) *(<)/g;
    var wsexp = / *(.*) +\n/g;
    var contexp = /(<.+>)(.+\n)/g;
    var attrExp = /("|\w) *(\w+=")/g
    var inAttrExp = /(;) */g;
    xml = xml.replace(reg, '$1\n$2')
             .replace(wsexp, '$1\n')
             .replace(contexp, '$1\n$2')
             .replace(attrExp, '$1\n$2')
             .replace(inAttrExp, '$1\n');
    var pad = 0;
    var formatted = '';
    var lines = xml.split('\n');
    var indent = 0;
    var lastType = 'other';
    // 4 types of tags - single, closing, opening, other (text, doctype, comment) - 4*4 = 16 transitions 
    var transitions = {
        'single->single'        : 0,
        'single->closing'       : -1,
        'single->opening'       : 0,
        'single->other'         : 0,
        'closing->single'       : 0,
        'closing->closing'      : -1,
        'closing->opening'      : 0,
        'closing->other'        : 0,
        'opening->single'       : 1,
        'opening->closing'      : 0, 
        'opening->opening'      : 1,
        'opening->other'        : 1,
        'other->single'         : 0,
        'other->closing'        : -1,
        'other->opening'        : 0,
        'other->ending'         : -2,
        'other->attribute'      : -1,
        'other->other'          : 0,
        'begining->attribute'   : 1,
        'ending->begining'      : 1,
        'ending->opening'       : 1,
        'ending->other'         : 1,
        'attribute->other'      : 1,
        'attribute->ending'     : -1
    };

    for (var i=0; i < lines.length; i++) {
        var ln = lines[i];
        var single = Boolean(ln.match(/<.+\/>/)); // is this line a single tag? ex. <br />
        var closing = Boolean(ln.match(/<\/.+>/)); // is this a closing tag? ex. </a>
        var opening = Boolean(ln.match(/<[^!].*>/)); // is this even a tag (that's not <!something>)
        var begining = Boolean(ln.match(/<[^!].*/));
        var ending = Boolean(ln.match(/.*>/));
        var attribute = Boolean(ln.match(/\w+ *= *".*/));
        var type = single ? 'single' : closing ? 'closing' : opening ? 'opening' : begining ? 'begining' : ending ? 'ending' : attribute ? 'attribute' : 'other';
        var fromTo = lastType + '->' + type;
        lastType = type;
        var padding = '';

        indent += (transitions[fromTo] || 0);
        for (var j = 0; j < indent; j++) {
            padding += '    ';
        }

        formatted += padding + ln + '\n';
    }

    return formatted;    
}

