function search(searchString) {    
    this.get('searchResultList').updateList([]);
    this.get('sourceText').setTextString('');
    if (!searchString || (searchString.length < 2)) {
        this.get('sourceText').setTextString('"' + searchString + '" is to short');
        return;
    }
    var finds = [], i = 0;
    function matches(s) {
        return s.toLowerCase().include(searchString.toLowerCase());
    } 

    // var re = new RegExp(searchString)
    var time = Functions.timeToRun(function() {
    this.withAllMethodsDo(function(object, eaMethod, type, optParent) {
        i++;
        var name;
        if (!object) return;

        if (object.constructor.prototype == object)
            name = object.constructor.name

        if (object.name) name = object.name;
        if (object.namespaceIdentifier)
            name = object.namespaceIdentifier

        if (object.constructor.prototype != object)
            name = name + "(object)"

        if ((object.constructor.prototype != object) && (object instanceof Morph))
            name = object.name + "(" + object.id + ")"

        if (object === Global)
            name = " ";

        if (type == "layered") {

           name = object._layered_object.constructor.type 
                + " (" + optParent.name + ")" 

        }

        var string = name + ": " + eaMethod;        
        var item = {object: object, method: eaMethod, string: string, type: type};
        if (type == "layered") {
          item.layerName = optParent.name
        }

        if (matches(eaMethod)) {
            item.search= 'implementor',
            finds.push(item)
            return;
        }    

        var f = object[eaMethod];
        if (!f || !f.getOriginal) return;
        var source = String(f.getOriginal())
        if (matches(source)){
            item.search =  'sender'
            finds.push(item)
        }    
    })
    }.bind(this))
    this.get('sourceText').setTextString("search " + i + " methods, found " 
        + finds.length + " methods in " + time + "ms");
    var items =  this.itemsFromFinds(finds)
    this.get('searchResultList').updateList(items);
}

