function browse(methodItem) {
    // methodItem = this.get('searchResultList').selection
    if (! methodItem) return;

    // deal with non method findings....
    if (methodItem.type == 'layer') {
        this.showLayerDefinition(methodItem)
        return
    } 

    // methodItem = this.get('searchResultList').selection
    var methodName = methodItem.method;
    var object = methodItem.object;
    var orgFunc = object[methodName];

    if (!orgFunc || !orgFunc.getOriginal) {
        this.get('sourceText').setTextString('no source');
        return
    }
    orgFunc = orgFunc.getOriginal()

    this.targetFunction = orgFunc;
    this.targetObject = object;

    var source = "// changes here only affect runtime\n", 
        tab = lively.morphic.Text.prototype.tab;
   if (methodItem.layerName) {
        var partialMethod = object[methodName];
        source +=  methodItem.layerName + ".refineClass(" + 
            object._layered_object.constructor.type + ", {\n" + 
            tab + methodName + ": " + partialMethod +"\n})";
    } else if (orgFunc.belongsToTrait) {
        source +=  "Trait('" + orgFunc.belongsToTrait.name + "', {\n" 
            + tab + methodName + ": " + orgFunc +"\n})";
    } else if (orgFunc.declaredClass) {
        source +=  orgFunc.declaredClass + ".addMethods({\n" 
            + tab + methodName + ": " 
            + orgFunc + "\n});"
    } else if(methodItem.type == 'extend') {
        // perhaps it is a an class object
        source +=  "Object.extend(" + (object.type || object.namespaceIdentifier) 
            + ",{\n" + tab + methodName + ": " 
            + orgFunc + "\n});"
    } else if(object.isMorph && (object.constructor.prototype !== object) ) {
        // display as script
        source =  "this.addScript(" + orgFunc + ");"
    } else if(object === Global) {
        // display as script
        source +=  "Object.extend(Global, {\n" + tab + methodName + ": " +
            orgFunc + "\n});"
    }

    this.get('sourceText').setTextString(source)

    module('lively.ide.SyntaxHighlighting').load(true);
    this.get('sourceText').highlightJavaScriptSyntax();

    var searchText = this.get('searchText').textString;
    var m = searchText.match(/[^a-zA-Z0-9_\.]/g)
    if(m) {
        m.forEach(function (each) {
            var escapedChar = '\\' + each;
            var position = searchText.search(
                new RegExp('[^\\\\]' + '\\' + each)) + 1;
            searchText = searchText.substring(0,position) 
                + escapedChar + searchText.substr(position + 1);
        });
    }
    this.get('sourceText').emphasizeRegex(new RegExp(searchText, 'g'), {color: Color.red});

    var info = '' + this.findSourceModule(orgFunc);

    if (orgFunc.belongsToTrait)
        info += ' (' +  orgFunc.belongsToTrait + " in " + orgFunc.belongsToTrait.sourceModule +")";

    this.get('infoText').setTextString(info)
}

