function withAllMethodsDo(func) {
    var visited = [];

    cop.allLayers().forEach(function(layer) {
        func(eval(layer.namespaceName), layer.name, 'layer')
        layer.layeredObjects().forEach(function(obj) {
            var layerDef = cop.getLayerDefinitionForObject(layer, obj)
            Functions.own(layerDef).forEach(function(eaMethod) {
                func(layerDef, eaMethod, 'layered', layer)
            });
        });
    });    


    Functions.own(Global).forEach(function(eaMethod) {
        func(Global, eaMethod, 'global')
    });    

    this.world().withAllSubmorphsDo(function(ea) {
        Functions.own(ea).forEach(function(eaMethod) {
            func(ea, eaMethod, 'script')
        });    
    });

    Global.subNamespaces(true).forEach(function(ea) {
        Functions.own(ea).forEach(function(eaMethod) {
            func(ea, eaMethod, 'extend')
        });    
    });

    Global.classes(true).uniq().forEach(function(eaClass) {
        Functions.own(eaClass).forEach(function(eaMethod) {
            func(eaClass, eaMethod, 'extend')
        });    

        Functions.own(eaClass.prototype).forEach(function(eaMethod) {
            func(eaClass.prototype, eaMethod, 'class')
        })    
    })
}

