function itemsFromFinds(finds) {
    var searchString = this.get('searchText').textString;
     var items = finds.collect(function(ea) {
        var pref = "" + ea.type + " ";
        if (ea.search == "sender") pref += "full: "
        if (ea.search == "implementor") pref += "name: "
        var string = pref + ea.string;
        var func = ea.object[ea.method];
        if (func.belongsToTrait) 
            string += ' / ' + func.belongsToTrait + '' 

        var result = {
            isListItem: true,
            value: ea,
            string: string,
            priority: {implementor: "B", sender: "C"}[ea.search] || "D",
        };
        if (ea.method == searchString) result.priority = "A"
        return result
    })
    items = items.sortBy(function(ea) {return  ea.priority + ea.string})
    return items
}

