function browseMethod() {
    alertOK('browseMethod')
    var methodItem = this.get('searchResultList').selection;
    if (methodItem.type == 'layer') {
        var layer = methodItem.object[methodItem.method];
        this.openMethodInSystemBrowser(methodItem.method, 
            undefined, layer.sourceModule);
        return
    }

    if (!this.targetFunction) return;
    var methodName =  this.targetFunction.methodName || this.targetFunction.displayName; 
    if(this.targetFunction.declaredClass) {
        var classObj = eval(this.targetFunction.declaredClass)
        this.openMethodInSystemBrowser(classObj.type, methodName);
        return;
    }
    if(this.targetFunction.belongsToTrait) {
        this.openMethodInSystemBrowser(this.targetFunction.belongsToTrait.name, methodName);
        return;
    }
    if(Class.isClass(this.targetObject)) {
        this.openMethodInSystemBrowser(this.targetObject.type, methodName);
        return;
    }
    if(this.targetObject.namespaceIdentifier) {
        this.openMethodInSystemBrowser(this.targetObject.namespaceIdentifier, methodName);
        return;
    }
    if(this.targetObject instanceof lively.morphic.Morph) {
        var editor = this.world().openObjectEditorFor(this.targetObject);
        editor.align(editor.bounds().center(), this.world().visibleBounds().center());
        return;
    }
    if (Class.isClass(this.targetFunction)) {
        // constructor
        this.openMethodInSystemBrowser(this.targetFunction.displayName);
        return;
    }

    alertOK('not implemented yet for ' + this.targetFunction)
}

