function openMethodInSystemBrowser(objectName, methodName, optSourceModule) {
    if (objectName)
        objectName = objectName.replace(/^Global\./,"");
    alert("browse " + objectName + ":" + methodName)
    // objectName = 'SelectionMorphTrait', methodName = 'onDrag'
    var sourceModule = optSourceModule ||
        this.findSourceModule(this.targetFunction);
    if (!sourceModule) 
        alert('could not browse, since no source module found!')
    var uri = new URL(sourceModule.uri()),
        relative = uri.relativePathFrom(new URL(Config.codeBase)),
        moduleNode = lively.ide.startSourceControl().addModule(relative),
        rootNode = moduleNode.ast();

    var fileFragments = rootNode.subElements(10).select(function(ea) {
            var path = ea.getOwnerNamePath()
            return path.include(objectName) 
                && (!methodName || path.include(methodName)) 
        });

    //alertOK("found following places to browse: " + fileFragments)
    if (fileFragments.length > 0) {
        fileFragments[0].browseIt()
    }

}

