function findSourceModule(funcObj) {
    if (this.targetFunction.belongsToTrait) 
        return this.targetFunction.belongsToTrait.sourceModule
    if (funcObj.sourceModule)
        return funcObj.sourceModule;
    // look in the class
    if (!funcObj.sourceModule && funcObj.declaredClass) {
        var classObj = eval(funcObj.declaredClass)
        return  classObj.sourceModule
    }
}

