function boundEval(str) {
    var result,
        that = this,
        ctx = that.getDoitContext()

    this.objectEditorPane.ensureAnnotationLayer();

    withLayers([ScriptAnnotationLayer], function() {    
        if (that.getDoitContext().isMorph || !str.include("this")) {
            result = $super(str);
        } else {
            var targets = ctx.targets
            result = targets.collect(function (t) {
                try {
                    that.doitContext = t
                    return $super(str)
                } catch(e) {
                    return e
                } finally {
                    that.doitContext = ctx
                }
            })
        }
    })
    return result
}

