function newMultiSelection() {
    var ms = {
        __multi_selection_proxy__: true,
        targets: [],
        highlights: [],
        setName: function (n) {
            this.name = n
        },
        getName: function () {
            if (!this.name) {
                return "Unnamed Group" + " [" + this.targets.length + "]"
            } else {
                return this.name + " [" + this.targets.length + "]"
            }
        },
        push: function (obj) {
            var idx = this.targets.indexOf(obj)
            if (idx < 0) {
                this.targets.push(obj)
            } else {
                this.targets.removeAt(idx)
            }
        },
        getFunction: function (name) {
            var funcs = this.privateTargetFunctions()
            return funcs.find(function(f) { return f[0] === name })[1]
        },
        targetFunctions: function () {
            var funcs = this.privateTargetFunctions()
            return funcs.collect(function(f) { return f[0] });
        },
        privateTargetFunctions: function () {
            if (!this.targets.length)
                return []

            var that = this;
            var funcs = Functions.own(this.targets[0]).collect(function (fname) {
                return [fname, that.targets[0][fname]]
            })
            this.targets.each(function(target) {
              //debugger
              funcs = funcs.select(function(f){
                return Functions.own(target).include(f[0]) &&
                        target[f[0]].toString() == f[1].toString();
              });
            });
            return funcs
        },
        highlight: function (rect) {
            this.removeHighlight()
            this.highlights = this.targets.collect(function (ea) {
                var r = rect.copy()
                ea.world().addMorph(r)
                r.update(ea)
                return r
            })
        },
        removeHighlight: function () {
            this.highlights.each(function (h) {
                h.remove()
            })
            this.highlights = []
        },
        addScript: function (func) {
            var fs = []
            this.targets.each(function (t) {
                fs.push(t.addScript(func))
            })
            return {
                tag: function (t) {
                    fs.each(function (f) { f.tag(t) })
                    return true
                }
            }
        },
        deleteSelectedScript: function (name) {
            this.targets.each(function (t) {
                delete t[name]
            })
        }
    }

    Functions.own(ms).each(function (name) { ms[name].asScriptOf(ms, name) })
    return ms
}

