function insertSpaces_noStrings(text) {

    text = text.replace(/(function|if|for|while|switch) *(\(.*\)) */g,"$1 $2 ");
    text = text.replace(/ *(\++=*) */g," $1 ");
    text = text.replace(/ *(\-+=*) */g," $1 ");
    text = text.replace(/ *(\*+=*) */g," $1 ");
    text = text.replace(/ *(\/+=*) */g," $1 ");
    text = text.replace(/ *(<+=*) */g," $1 ");
    text = text.replace(/ *(>+=*) */g," $1 ");
    text = text.replace(/ *(:) */g," $1 ");
    text = text.replace(/ *(%=*) */g," $1 ");
    text = text.replace(/ *([\+\-\*\/<>!%]?=+) */g," $1 ");
    text = text.replace(/, */g, ", ");
    text = text.replace(/ *(.*[^ ]) *\n/g, "$1\n");
    text = text.replace(/ *\n/g, "\n");
    text = text.replace(/ +/g, " ");

    return text;
}

