function insertLinebreaks_noStrings(text) {

    text = text.replace(/ *\n/g, "\n");
    text = text.replace(/ *(.*[^ ]) *\n/g, "$1\n");

    var i = 0;
    var forHeads = {};
    while(text.match(/(for *\()([^\)]*;[^\)]*;[^\)]*)(\))/)){
        forHeads[i] = text.match(/(for *\()([^\)]*;[^\)]*;[^\)]*)(\))/)[2];
        text = text.replace(/(for *\()([^\)]*;[^\)]*;[^\)]*)(\))/, "$1\u0009"+i+"$3");
        i++;
    }

    var newText = text;
    text = '';    
    while(newText !== text){    
        text = newText;
        newText = newText.replace(/(;|{) *([^\n])/g, "$1\n$2");
        newText = newText.replace(/([^\n]) *(})/g, "$1\n$2");
        newText = newText.replace(/(}(,|;)) *([^\n])/g, "$1\n$3");
        newText = newText.replace(/(}) *(^[\n,;])/g, "$1\n$2");
    }

    while(i > 0){
        i--;
        text = text.replace(new RegExp("\u0009"+i),forHeads[i].replace(/; */g,"; "));
    } 

    return text;
}

