function fileFragmentAtOffset(filename, offset) {
        filename = filename.replace(/^core\//, "")
        alertOK("filename " + filename)
	var sc = lively.ide.startSourceControl(),
		url = URL.codeBase.withFilename(filename),
		moduleWrapper = sc.addModule(filename),
		parseMaxDepth = 5;
	return !moduleWrapper ? undefined : moduleWrapper.ast().getSubElementAtIndex(offset, parseMaxDepth)
}

