function searchCode(string) {

    var listMorph = this.get('searchList'),
        depth = Number(this.get('depthText').textString),
        filePattern = this.get('filePatternText').textString,
        inDir = this.get('dirText').textString,
        search = new ServerSideCodeSearch(string, depth, filePattern, inDir);
    connect(search, 'results', listMorph, 'updateList');
    connect(search, 'results', this.get('searchStatus'), 'setTextString', {
        converter: function(results) {
            return "found " + results.length + " matches"
        }
    });
    this.get('searchList').updateList([]);
    this.get('searchStatus').setTextString('searching... for ' + string)
    this.get('codeText').setTextString('');
    search.searchJSCode()

}

