function selectedFileFragment() {
	var selection = this.get('searchList').selection;
	if (!selection) return;
	var match= selection.match(/(.+\.js):([0-9]+):.*/)
	if (!match) return; 
	var dir = this.get('dirText').textString.replace(/^\//,"");
	return this.fileFragmentAtOffset(dir + match[1], Number(match[2]))
}

