function selectCodeFragment() {
	// display the code that was found
	var foundCode = this.get('searchList').selection,
		searchTerm = this.get('searchField').textString;
	var codeFragment = '', idx;
	while ((idx = foundCode.indexOf(searchTerm)) >= 0) {
		var endIdx = idx + searchTerm.length;
		codeFragment += foundCode.slice(idx-40, endIdx+40);
		codeFragment += '\n\n-=-=-=-=-=-=-=-=-=-=-\n\n'
		foundCode = foundCode.slice(endIdx, foundCode.length);
	}
	this.get('codeText').textString = codeFragment;
	return true;
}

