function showSource(jso) {
    this.showClassHistoram(jso)

    alertOK("show source of " + jso)
var printObjectTreeValue = function(value, registry, visited, depth) {
    if (value && value.id) value = printObjectTree(registry, value.id, visited, depth + 1)
    if (Object.isArray(value)) {
        value = "[" + value.collect(function(eaArrayElement) {
            return printObjectTreeValue(eaArrayElement, registry, visited, depth + 1)
        }) + "]"
    }
    return value
}

var printObjectTree = function(registry, id, visited, depth) {
    if (visited.include(id))
        return "{ -> " + id + "}"
    visited.push(id)
    var obj = registry[id];
    var result = "{__REG_ID__:" + id + "\n"
    if (obj) {
        Properties.own(obj).forEach(function(ea) {
            result += Strings.indent(ea + ": ", "  ", depth) + 
                printObjectTreeValue(obj[ea], registry, visited, depth + 1) +",\n"
        })
    }
    result += Strings.indent(id + "}", "  ", depth)
    return result
}

    var source = printObjectTree(jso.registry, 0, [], 0)

    this.get('SourceCode').textString = source
}

