function showSourceOfObject(obj) {
    var serializer = lively.persistence.Serializer.createObjectGraphLinearizerForCopy();
    serializer.showLog = false;
    var copyPlugin = new CopyOnlySubmorphsPlugin();
    copyPlugin.root = obj;
    serializer.addPlugin(copyPlugin);
    serializer.copy
    var jso = serializer.serializeToJso(obj);
    this.showSource(jso)
}

228.source:
function showClassHistoram(jso) {
    var classHistogram = {}
    // moduleHistogram = {}

    Properties.values(jso.registry).each(function(ea) {
        var c = ea["__LivelyClassName__"]
        if (!classHistogram[c]) { classHistogram[c] = 0 };
        classHistogram[c]++
    })

    this.get('Analysis').textString = Properties.own(jso.registry).length + " objects\n" 
        + 'size: ' + (JSON.stringify(jso).length)+ " byte\n";
    this.get('Analysis').textString += 'Class Histogramm:\n' + 
        Properties.own(classHistogram)
        .sortBy(function(ea) { return Number(classHistogram[ea])})
        .reverse()
        .collect(function(ea) {[ea]
            return ea + ": " + classHistogram[ea]
        }).join("\n")
    return classHistogram
}

