function testIdenticalRectangle(syncMorph) {
    var snapshotA = this.serialize(syncMorph, this.table);
    var snapshotB = this.serialize(syncMorph, this.table);
    var diff = syncMorph.diff(snapshotA, snapshotB);
    this.assertEquals(diff, undefined, 'no diff for identical morphs');
}

function testMovedRectangle(syncWorld) {
    var snapshotA = this.serialize(syncWorld, this.table);
    var oldX = this.rect._Position.x;
    var oldY = this.rect._Position.y;
    this.rect.moveBy(pt(10,20));
    var snapshotB = this.serialize(syncWorld, this.table);
    var diff = syncWorld.diff(snapshotA, snapshotB);
    var expected = {};
    expected[this.rect.id + "/_Position"] = {x: [oldX+10], y: [oldY+20]};
    this.assertMatches(expected, diff);
    this.assertMatches(diff, expected);
}

function testRemoveMorph(syncWorld) {
    var snapshotA = this.serialize(syncWorld, this.table);
    var snapshotB = this.serialize(syncWorld, {});
    var diff = syncWorld.diff(snapshotA, snapshotB);
    var expected = {};
    expected[this.rect.id] = [0,0];
    this.assertMatches(expected, diff);
    this.assertMatches(diff, expected);
}

function testResizedRectangle(syncWorld) {
    var snapshotA = this.serialize(syncWorld, this.table);
    this.rect.setExtent(pt(400,20));
    var snapshotB = this.serialize(syncWorld, this.table);
    var diff = syncWorld.diff(snapshotA, snapshotB);
    var expected = {};
    expected[this.rect.id + "/shape/_Extent"] = {x: [400], y: [20]};
    this.assertMatches(expected, diff);
    this.assertMatches(diff, expected);
}

