function reset() {
    this.get("SyncWorldConnectButton").changeAppearanceFor(false, false);
    this.get("SyncWorldSyncButton").changeAppearanceFor(false, false);
    require('users.cschuster.sync.client').toRun(function() {
        this.wc = new users.cschuster.sync.WorkingCopy();
        this.wc.addPlugin(new users.cschuster.sync.MorphPlugin());
        connect(this.wc, "rev", this.get("RevLabel"), "textString", {
            converter: function (val) {
                return "R " + val;
            }
        });
        console.log("initialized");
    }.bind(this));
}

