function sync(activate) {
    if (activate) {
        if (!this.get("SyncWorldConnectButton").value) return;
        this.wc.startSyncing();
        console.log("started syncing");
    } else {
        this.wc.stopSyncing();
        console.log("stopped syncing");
    }
}
