function log(msg) {
   this.addLine(Strings.format.call(null, msg, Array.from(arguments).splice(1)), {color: Color.web.darkgray.darker()});
}

24.source:
function addLine(msg, style) {
    var chunk = this.createChunk();
    chunk.textString = "\n" + new Date().format("HH:MM:ss") + " " + msg;
    chunk.styleText(style);
    if (this.getScrollableNode()) this.scrollToBottom();
}

