104.source:
function reset() {
    if (this.layer) this.layer.beNotGlobal();
    this.layer = null;
    this.setFill(this.normalFill = this.owner.menuButton.normalFill);
    this.doNotSerialize = ['layer'];
}

107.source:
function onFire() {
    if (!this.layer) this.createLayer();
    if (this.layer.isGlobal()) {
        this.layer.beNotGlobal();
    } else {
        this.layer.beGlobal();
    }
    this.setFill(this.normalFill = (this.layer.isGlobal() ?
        Color.green : this.owner.menuButton.normalFill));
}

110.source:
function createLayer() {
    var main = this.get("SystemConsoleMain");
    this.layer = cop.createLayer("GlobalStatus").refineClass(lively.morphic.World, {
        setStatusMessage: function (msg, color) {
            main.addLine(msg, {color: color});
        }
    });
}

