function showTestRunResultCallback() {
    var time = new Date() - this.startTime;
    var infoString = time.toString() + 'ms for ' 
    infoString += this.numberOfTestCases + ' tests';

    if (this.testSuite.result.failed.length == 0) {
        this.displayResult(infoString, Color.green);
    } else {
        var failString = infoString + '\n\n';
        failString += this.testSuite.result.failed.length + ' failed tests:\n\n';
        failString += this.testSuite.result.failed.collect(function(result) {
            var msg = result.classname + '>>' + result.selector;
            msg += '\n' + result.err.message;
            msg += '\n' + result.err.stack;
            return msg;
        }).join('\n---------\n');
        this.displayResult(failString, Color.red);
    }

    this.get('TestRunProgressIndicator').hide()

    if (this.owner.isCiJob) {
        this.owner.reportCiResult();
    }
}

