function loadSelectedModules() {
    this.get('ModuleProgressIndicator').show();
    var moduleNames = this.get('ModuleList').getSelectedItems().collect(function(each) {
        return each.value;
    }); 

    this.loadedModules = [];
    module('lively.TestFramework').load();
    var suite = new TestSuite(); 
    moduleNames.forEach((function(each) {
        (function() {
            var testModule = module(each);
            testModule.load();
            this.loadedModules.push(testModule);
            suite.addTestCasesFromModule(testModule);

            if (this.loadedModules.length === moduleNames.length) 
                this.setTestClasses(suite.testCaseClasses)
        }).bind(this).morphicDelay(0);
    }).bind(this));

    this.get('ModuleProgressIndicator').hide();
}

