function loadModulesAndRunTheirTests() {
    var callback = (function() {
        this.selectAllTestClasses();
        this.runSelectedTestClasses();
    }).bind(this);

    var list = this.get('TestClassesList');
    connect(list, 'itemList', {cb: callback}, 'cb', {removeAfterUpdate: true, converter: function(val) { this.getSourceObj().setList(val); }});

    this.selectAllModules();
    this.loadSelectedModules();
}

