function runSelectedTestClasses(onFinished) {
    this.startTime = new Date();
    this.get('TestRunProgressIndicator').show();

    module('lively.TestFramework').load(true);
    this.testSuite = new TestSuite();

    var selectedTestClasses = this.get('TestClassesList').getSelections().
        collect(function(ea) { return Class.forName(ea) });

    this.testSuite.addTestCases(selectedTestClasses);

    this.numberOfTestCases = this.testSuite.testCaseClasses.inject(0, function(sum, testClass) {
        return sum + new testClass().allTestSelectors().length;
    });

    this.testSuite.runFinished = (function(){
        this.showTestRunResultCallback();
        if (onFinished) onFinished();
    }).bind(this);
    this.testSuite.runAll();
}

