function runCiJob() {
    this.reset();

    this.isCiJob = true;

    var job = this.latestJob();
    var testId = this.testId = job.testId;
    var modules = job.modules;

    modules.forEach((function (each) {
        this.testRunner.addModule(each);
    }).bind(this));

    this.testRunner.loadModulesAndRunTheirTests();
}

