function reportCiResult() {
    var testId = this.testId;
    var db = this.couchDB();
    var jobResults = db.open('last_test_result');
    alert(this.testRunner.testSuite.result.failed.length);
    jobResults.testId = this.testId;
    jobResults.message = this.get('ResultPane').getTextString();
    jobResults.timestamp = new Date();
    jobResults.modules = this.testRunner.loadedModules.toString();
    jobResults.testsRun = this.testRunner.numberOfTestCases;
    jobResults.testsFailed = this.testRunner.testSuite.result.failed.length;
    jobResults.testsPassed = this.testRunner.numberOfTestCases - this.testRunner.testSuite.result.failed.length;
    jobResults.failed = this.testRunner.testSuite.result.failed;
    db.save(jobResults);
    this.isCiJob = undefined;
    this.testId = undefined;
}

