function onDrag(evt) {
    // alert("onDrag")
    // this.owner.setPosition(pt(0,0))
    // this.owner.setOrigin(pt(0,0))
    var moveDelta = evt.mousePoint.subPt(this.dragStartPoint)

    // moveDelta = moveDelta.maxPt(pt(0,0))
    var ownerExtent = this.owner.getExtent();

    // TODO does not work
    if (evt.isShiftDown()) {
        var maxDelta = Math.max(moveDelta.x, moveDelta.y);
        if (maxDelta == 0) return;
        var factor = ownerExtent.x /  ownerExtent.y
        moveDelta = pt(maxDelta * factor, maxDelta )
    }
    var accessor = "with" + this.cornerName.charAt(0).toUpperCase() + this.cornerName.substring(1);

    var newCorner = this.originalTargetBounds[this.cornerName]().addPt(moveDelta);
    var newBounds = this.originalTargetBounds[accessor ](newCorner);

    this.owner.setBounds(newBounds);

    // var newExtent = this.originalTargetExtent.addPt(moveDelta)
    // alertOK("new extent" + newExtent)
    // this.owner.setExtent(newExtent);
    this.owner.submorphs.select(function(ea) {
        return ea.isResizeCorner
    }).invoke('alignToOwner');
    if (this.owner.halos) {
        this.owner.halos.invoke('alignAtTarget')
    }
    // alertOK("relative " + this.relativeOrigin)
    var newOrigin =  pt(0,0).extent(this.owner.getExtent()).relativeToAbsPoint(this.relativeOrigin)

    // TODO IMPLEMENT updating the origin does not work yet
    // this.owner.setOrigin(newOrigin);
    // alertOK("new origin " + newOrigin)

}

