function getObserveTextLayer() {
    if (this.layer) return this.layer;
    var layerName = 'ObserveTextFocusLayer', counter = 1;
    while (Global[layerName + counter]) { counter++ }
    layerName += counter;
    this.layer = cop.create(layerName);
    this.setWithoutLayers([this.layer]);
    return this.layer;
}

