function updateFontFamily(value) {
    var m = this.targetMorph, selRange = m.priorSelectionRange;
    cop.withoutLayers(this.getLayers(), function() {
        if (!selRange || selRange[0] == selRange[1]) { m.setFontFamily(value); return }
        else m.changeEmphasis(selRange[0], selRange[1], function(prevEmph, doEmph) {
            doEmph({fontFamily: value})
        })
