995.source:
function showPage(page) {
    this.clearPage();
    this.currentPage = page;
    var that = this;
    page.morphs.forEach(function(ea) {
        that.addMorph(ea);});

}

998.source:
function getPage(pageNumber) {
    var page = null;
    this.pages.forEach(function(ea) {
        if (ea.pageNumber == pageNumber) {
            page = ea;
        }});
    return page;
}

1004.source:
function nextPage() {
    this.sortPages();
    var nextIndex = (this.pages.indexOf(this.currentPage) + 1) % this.pages.length;
    this.showPage(this.pages[nextIndex]);
}

1010.source:
function savePageAs(pageNumber) {
    var page = this.getPage(pageNumber);
    if (page === null) {
        this.pages.push({ 
            pageNumber: pageNumber,
            morphs: this.submorphs
        });
    } else {
        page.morphs = this.submorphs;
    }
}

1016.source:
function previousPage() {
    this.sortPages();
    var nextIndex = (this.pages.indexOf(this.currentPage) - 1);
    if (nextIndex < 0) nextIndex = this.pages.length - 1;    
    this.showPage(this.pages[nextIndex]);
}

1019.source:
function addMorph(morph, optMorphBefore) {
    if (morph.isText) morph.doitContext = this;
    return $super(morph, optMorphBefore);
}

