function loadWorld() {
    this.get('Status').textString = 'loading ... ' 
    var url = this.getWorldURL(),
        wr = new WebResource(url),
        content = wr.get().content,
        xml = jQuery.parseHTML(content),
        node = xml.find(function(ea) { 
            return ea.type == "text/x-lively-world" }),
        json = $(node).text();
