    var jso = JSON.parse(json);

    var world;
    // cop.withLayers([DevLayer], function() {
        world = lively.persistence.Serializer.deserializeWorldFromJso(jso);
    // })
    this.clearWorldContainer()
    if (!world) {
        alert("could not deserialize " + world)
        return
    }
    world.submorphs.each(function(ea) {
        var pos = ea.getPosition();
        this.get("WorldContainer").addMorph(ea)
        ea.setPosition(pos)
    }, this)
    this.get('Status').textString = '' 
}

