function becomeWorld() {
    // Ok, lets do a magic trick!

    var targetWorld = this.world()
    var sourceWorld = this.get("WorldContainer").submorphs[0]

    var tmp = targetWorld.submorphs.clone();
    targetWorld.submorphs.clone().invoke('remove')

    sourceWorld.submorphs.clone().forEach(function(ea) {
        targetWorld.addMorph(ea)
    })
    tmp.forEach(function(ea) {
        sourceWorld.addMorph(ea)
    })
    targetWorld.addMorph(this.owner)

}

