    var url  = new URL(this.get("List").selection);
    this.world().prompt('Rename world: ',  
        function( filename) {
            var toURL = url.withFilename(filename)
            new WebResource(url)
                .statusMessage('Renamed ' + url, 'Error renaming ' + url + ' ' + toURL, true)
                .moveTo(toURL);
            this.updateLinks();
            // this.get("List").setSelection(toURL)
        }.bind(this), url.filename() )
}

