function displayVersions(versions) {
    this.get('VersionsList').setList(versions.collect(function(ea) {
            return {
                    string: ea.rev + ' ' + ea.date.format("yyyy-mm-dd HH:MM") 
                        + ' ' + ea.author,
                    value: ea
                }
        })
    )
}
