function loadWorld(url) {
    this.get('Status').textString = 'loading ... '
    if (!url) {
        this.updateVersions()
    }

    var url = url || this.getWorldURL(),
        wr = new WebResource(url),
        xml = wr.get().contentDocument,
        json = $(xml).find("#LivelyJSONWorld").text();

    if (!json) {
        this.clearWorldContainer();
        this.get('Status').textString = 'no world found!' 
        return
    }

    var world = this.deserializeWorldFromJSON(json)
    this.clearWorldContainer()
    this.get("WorldContainer").addMorph(world)
    this.get('Status').textString = '' 
}

