function showHistory() {
    if (this.augmented) this.augmented.remove()
    var worldRevs = Properties.own(this.worlds).sort()
    var world = this.worlds[worldRevs.last()]
    this.augmented = Morph.makeRectangle(world.bounds())
    this.augmented.setFill(null)
    this.augmented.setBorderWidth(null)
    this.augmented.ignoreEvents()
    this.augmented.disableEvents()

    world.submorphs.each(function(ea) {
        var m = Morph.makeRectangle(ea.bounds());
        m.setFill(0)
        m.setBorderWidth(2)
        m.setBorderColor(CrayonColors.tangerine)
        var label = new lively.morphic.Text(new Rectangle(0,0,100,20),"" + ea.id)
        label.beLabel()
        label.setFontSize(8)
        label.setTextColor(CrayonColors.tangerine)
        label.align(
            label.bounds().bottomLeft(),
            m.shape.bounds().topLeft())
        m.addMorph(label)
        this.augmented.addMorph(m)
    }, this)
    this.get('WorldContainer').addMorph(this.augmented)
    // this.get('WorldContainer').submorphs.last().remove()

}

