function loadWorldAsync(url, rev) {
    var versionedURL = new WebResource(url).createResource()
        .createVersionURLString(rev);
    var wr = new WebResource(versionedURL).beAsync();
    lively.bindings.connect(wr, 'contentDocument', {
        cb: function(xml) {
            this.parseWorldJSON(
                url, rev, $(xml).find("#LivelyJSONWorld").text())
        }.bind(this),
    }, 'cb')
    lively.bindings.connect(wr, 'progressEvent', this.get('ProgressBar'), 'setValue',
            {converter: function(rpe) { return (rpe.loaded / rpe.total) }});
    wr.get()
}

