function renderNode(aNode) {
    var morphNode = aNode.getVisuals();
    var leftWidth = 100;
    var rightWidth = 100;
    var p;
    if(aNode.leftChild) {
        var leftChild = this.renderNode(aNode.leftChild);
        leftWidth = leftChild.morphNode.getBounds().width+(leftChild.treeNode.leftChild ? 20 : 0)+(leftChild.treeNode.rightChild ? 20 : 0)+10;
    }
    if(aNode.rightChild) {
        var rightChild = this.renderNode(aNode.rightChild);
        rightWidth = rightChild.morphNode.getBounds().width+(rightChild.treeNode.leftChild ? 20 : 0)+(rightChild.treeNode.rightChild ? 20 : 0)+10;
        rightChild.morphNode.setPosition(pt((leftWidth || 0) + rightWidth/2, 150));
    }
    var total = leftWidth + rightWidth;
    if(aNode.leftChild) {
        p = -total/2 + leftWidth/2;
        leftChild.morphNode.setPosition(pt(p,150));
        var edge = new lively.morphic.Path([pt(50,100), pt(p+50,150)]);
        edge.setBorderWidth(5);
        morphNode.addMorph(edge);
        morphNode.addMorph(leftChild.morphNode);
    }
    if(aNode.rightChild) {
        p = -total/2 + leftWidth + rightWidth/2;
        rightChild.morphNode.setPosition(pt(p,150));
        var edge = new lively.morphic.Path([pt(50,100), pt(p+50,150)]);
        edge.setBorderWidth(5);
        morphNode.addMorph(edge);
        morphNode.addMorph(rightChild.morphNode);
    }

    // render the action (left rotate or right rotate)
    if(aNode.currentAction) {
        var args = aNode.currentAction.split(":");
        if(!aNode.parent) {
            morphNode.setOrigin(pt(0,0));
        }
        switch(args[0]) {
            case 'rotateLeft':
                this.addRotateLeft(morphNode);
                break;
            case 'rotateRight':
                this.addRotateRight(morphNode);
                break;
            case 'goLeft':
                this.addGoLeft(morphNode, args[1]);
                break;
            case 'goRight':
                this.addGoRight(morphNode, args[1]);
                break;
            case 'remove':
                this.addRemove(morphNode);
                break;
        }
        if(!aNode.parent) {
            morphNode.setOrigin(pt(50,0));
        }
    }

    if(!aNode.parent) {
        this.rootMorph = morphNode;
        this.addMorph(morphNode);
        //aNode.setOrigin(pt(aNode.getBounds().x + aNode.getBounds().width/2,0));
        morphNode.setOrigin(pt(50,0));
        morphNode.setScale(Math.min(Math.min(this.getExtent().x/morphNode.getBounds().width - 0.03,(this.getExtent().y-60)/morphNode.getBounds().height - 0.03),1));
        morphNode.setPosition(pt(this.getExtent().x/2, 40));
    }


    return {morphNode: morphNode, treeNode: aNode};
    //return aNode.getBounds().width+(aNode.leftChild ? 20 : 0)+(aNode.rightChild ? 20 : 0)+10;
}

