function renderNode(aNode) {
    //aNode.removeAllMorphs();
    var morphNode = aNode.getVisuals();
    var combinedChildren = [];
    for(var i = 0; i < aNode.children.length; i++) {
        combinedChildren.push(aNode.children[i]);
        if(aNode.danglingChild && aNode.danglingChild.index === i) {
            combinedChildren.push(aNode.danglingChild.node);
        }
    }
    if(combinedChildren.length > 0) {
        var widths = [];
        var totalWidth = 0;
        var that = this;
        var childNodes = [];
        var edges = [];
        combinedChildren.forEach(function(ea) {
            var childNode = that.renderNode(ea);
            var w = childNode.morphNode.getBounds().width+(childNode.treeNode.children ? childNode.treeNode.children.length : 0)*20+10;
            totalWidth += w;
            widths.push(w);
            childNodes.push(childNode);
        });
        for(var i=0; i < combinedChildren.length; i++) {
            var p = -totalWidth / 2;
            for(var j = 0; j < i; j++) {
                p += widths[j];
            }
            p += widths[i] / 2;
            childNodes[i].morphNode.setPosition(pt(p, 80));
            if(!aNode.danglingChild || childNodes[i].treeNode !== aNode.danglingChild.node) {
                var id = i;
                if(aNode.danglingChild && i > aNode.danglingChild.index) {
                    id--;
                }
                var edge = new lively.morphic.Path([pt(id*33,30), pt(p+50,80)]);
                edge.setBorderWidth(3);
                morphNode.addMorph(edge);
                edges.push(pt(p+50,80));
            }
            morphNode.addMorph(childNodes[i].morphNode);
        }
    }


    // render the action (left rotate or right rotate)
    if(aNode.currentAction) {
        if(!aNode.parent) {
            morphNode.setOrigin(pt(0,0));
        }

        var args = aNode.currentAction.split(":");
        switch(args[0]) {
            case 'goDown':
                this.addGoDown(morphNode, args[1], args[2], edges[args[1]]);
                break;
            case 'split':
                this.addSplit(morphNode, args[1]);
                break;
            case 'remove':
                this.addRemove(morphNode, parseInt(args[1]));
                break;
        }
        if(!aNode.parent) {
            //morphNode.setOrigin(pt(50,0));
        }

    }
    if(aNode === this.rootNode) {
        this.addMorph(morphNode);
        this.rootMorph = morphNode;
        morphNode.setOrigin(pt(morphNode.getBounds().x + morphNode.getBounds().width/2,0));
        morphNode.setScale(Math.min(Math.min((this.getExtent().x-20)/morphNode.getBounds().width - 0.01,(this.getExtent().y-60)/morphNode.getBounds().height - 0.01),1.5));
        morphNode.setPosition(pt(this.getExtent().x/2, 40));
    }

    return {morphNode: morphNode, treeNode: aNode};
}

