function draw() {
    var node = this.data;
    var root = node;

    this.chart = this.getChart();
    this.chart.selectAll("div").remove()

var margin = {top: 0, right: 0, bottom: 0, left: 0},
    width = this.getExtent().x,
    height = this.getExtent().y;

var authorColor = d3.scale.category20();

var treemap = d3.layout.treemap()
    .size([width, height])
    .sticky(true)
    .value(function(d) { return d.size; });

 var vis = this.chart.append("div")
    .style("position", "relative")
    .style("width", (width + margin.left + margin.right) + "px")
    .style("height", (height + margin.top + margin.bottom) + "px")
    .style("left", margin.left + "px")
    .style("top", margin.top + "px");

  var owner = this.owner;
  var select = function(n) {
      owner.select(n)
  }
  var node = vis.datum(root).selectAll(".node")
      .data(treemap.nodes)
    .enter().append("div")
      .attr("class", "node")
      .call(position)
      .style("background", function(d) {
          // return authorColor(d.creator)
          n = Date.now() - d.lastmodified.getTime();
          // distributed over the last two year
          var n = n / 1000 / 3600 / 24 / 356 / 2
         var l = Math.min(Math.max(1 - n, 0.2), 0.9)
         return d3.hsl(0,0,l)
       })
      .on("click", function(d) { 
        try {
            select(d)
        } catch(e) { $world.logError(e)}}, true)
      .text(function(d) { 
        if(d.dx < 40) return 
        return d.name; })
      .style("color", function(d) {
          return authorColor(d.creator)
       })
      .style("font-size", function(d) {
          return ((d.dx / 12) + 5) + "px"
       });


  d3.selectAll("input").on("change", function change() {
    var value = this.value === "count"
        ? function() { return 1; }
        : function(d) { return d.size; };
    node
        .data(treemap.value(value).nodes)
        .transition()
        .duration(1500)
        .call(position);
  });


function position() {
  this.style("left", function(d) { return d.x + "px"; })
      .style("top", function(d) { return d.y + "px"; })
      .style("width", function(d) { return Math.max(0, d.dx - 1) + "px"; })
      .style("height", function(d) { return Math.max(0, d.dy - 1) + "px"; });
}


}

