function exampleData() {
   this.data = {children: []}
   var names = ["A", "B", "C", "E", "F", "G"]
   var packages = ["P1", "P1", "B1", "P2"]
   var addRandomChildren = function(node, n, cont) {
        if (!node.children) node.children = [];
        for (var i=0; i < n; i++) {
            var name = names[Math.floor(Math.random() * names.length)];
            var package = packages[Math.floor(Math.random() * packages.length)];
            var child = {
                name: name,
                size: Math.random() * 100}
            if (cont) addRandomChildren(child, 3, false);
            node.children.push(child)
        }
    };
    addRandomChildren(this.data, 5, true)
}

