function webResourceToTree(wr, depth) {
    var props = this.getProps(wr.getURL())
    var node = {
            name: wr.getURL().filename(),
            url: wr.getURL(),
            creationdate: new Date(props["lp1:creationdate"]),
            lastmodified: new Date(props["lp1:getlastmodified"]),
            creator: props["lp1:creator-displayname"],
            size: Number(props["lp1:getcontentlength"])
    };
    if (depth > 0) {
        wr.getSubElements(1)
        var children = wr.subCollections.concat(wr.subDocuments)

        node.children = children.collect(function(ea) { 
            return this.webResourceToTree(ea, depth - 1)
        }.bind(this))
    }
    return node
}

