function getProps(url) {
    var wr = new WebResource(url)
    wr.propfind(0)
    $xml = $(wr.contentDocument)
    $($xml.find("getcontentlength")).text()
    var prop = {}
    $($xml.find("prop")).children().each(function() {
        prop[this.tagName] = $(this).text()
    })
    return prop
}

