function updateTweets() {
    var self = this;
    this.removeTweets();
    this.getTweets(function(tweets) {
        for (var i = 0; i < tweets.length; i++) {
            var t = tweets[i];
            self.addItem(t.from_user, t.profile_image_url, t.text);
        }
    });
}

