function getTweets(callback) {
    var url = "http://search.twitter.com/search.json";
    url += "?q=" + this.keyword;
    url += "&rpp=10&callback=?";
    var webR = new WebResource(url)
    connect(webR, 'content', {cb: callback}, 'cb', {
        converter: function(str) {
            try {
                return JSON.parse(str).results;
            } catch(e) {
                var msg = 'Error when getting tweets: ' + e;
                return msg
            }
        }})
    webR.get()
}

